    private void phiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_phiActionPerformed
        String uid = uidTxt.getText();
        String pw=new String(pwTxt.getPassword());
        User s = User.login(uid, pw);
        if(s==null) JOptionPane.showMessageDialog(this, "Error!!!");
        else JOptionPane.showMessageDialog(this, "Welcome to " + s.getFullName());
    }//GEN-LAST:event_phiActionPerformed

